        header {
  padding: 0.25rem 0.5rem;
}

.hero{
    display: flex;
    align-items: center;
    /* justify-content: center; */
    height: 90vh;
    text-align: center;
    margin: 1rem auto;
    background-color: #f4f5f1f1;
    
  }
  .home-badge{
    background-color: pink;
    width: 40%;
    margin: auto;
    padding: 10px 0;
    border-radius: 30px;
  }

  .hero__heading{
    font-size: 75px;
    font-family: Georgia, 'Times New Roman', Times, serif;
    font-weight: 500;
    width: 80%;
    line-height: 70px;
    margin: auto;
    padding: 10px 0;
  }

  .home-type-body-large{
    padding: 5px 0;
    font-size: 22px;
  }

  .margin-t-32 {
    text-decoration: none;
    color: #fff;
    background-color: #000;
    padding: 20px 0;
    width: 190px;
    margin-top: 20px;
    border-radius: 30px;

  }

.navigation {
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: #fff;
  position: sticky;
  top: 0;
  left: 0;
  z-index: 99;
  width: 100%;
  gap: 1.5rem;
}

.nav-links {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 2.5rem;
}

.navigation img {
  width: 180px;
  height: 80px;
}

.list {
  display: flex;
  list-style: none;
  gap: 1.5rem;
}

.list li {
  font-size: 18px;
  font-family: sans-serif;
}

.list li a {
  color: #000;
  text-decoration: none;
}
.list li a:hover,
.list li a.active {
  color: #000;
  border-bottom: 2px solid #000;
}

.start {
  display: flex;
  /* justify-content: right; */
  align-items: center;
  justify-content: space-between;
}
.start .btnn {
  background: #000;
  padding: 18px 30px;
  color: #fff;
  border-radius: 40px;
  border: 1px solid #fff;
}

.start .btnnn {
  color: #000;
  font-size: 16px;
  padding: 12px 30px;
  border-radius: 40px;
  border: 1px solid #fff;
}

button {
  border: none;
  cursor: pointer;
  padding: 18px 30px;
  opacity: 0.9;
}

button:hover {
  opacity: 1;
}

.menu-bar {
  width: 60px;
  height: 40px;
  display: none;
}

  /* Pop ups for login */
  .cancelbtn1, .deletebtn1 {
  float: left;
  width: 50%;
}

/* Add a color to the cancel button */
.cancelbtn1 {
  background-color: #ccc;
  color: black;
}


/* Add a color to the delete button */
.deletebtn1 {
  background-color: #000;
  color: #fff;
}

/* Add padding and center-align text to the container */
.container1 {
  padding: 16px;
  text-align: center;
}

/* The Modal (background) */
.modal1 {
  display: none; /* Hidden by default */
  position: fixed; /* Stay in place */
  z-index: 1; /* Sit on top */
  left: 0;
  top: 0;
  width: 100%; /* Full width */
  height: 100%; /* Full height */
  overflow: hidden; /* Enable scroll if needed */
  background-color: transparent;
  padding-top: 50px;
}

/* Modal Content/Box */
.modal-content1 {
  background-color: #fefefe;
  margin: 10% auto 15% auto; /* 5% from the top, 15% from the bottom and centered */
  border: 1px solid #888;
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
  width: 50%;
  height: 200px; /* Could be more or less, depending on screen size */
}

/* Style the horizontal ruler */
hr {
  border: 1px solid #f1f1f1;
  margin-bottom: 25px;
}

/* The Modal Close Button (x) */
.close1 {
  position: absolute;
  right: 18%;
  top: 15%;
  font-size: 40px;
  font-weight: bold;
  color: #000;
}

.close1:hover,
.close1:focus {
  color: #f44336;
  cursor: pointer;
}

/* Clear floats */
.clearfix1::after {
  content: "";
  clear: both;
  display: table;
}

/* Change styles for cancel button and delete button on extra small screens */
@media screen and (max-width: 300px) {
  .cancelbtn1, .deletebtn1 {
    width: 100%;
  }
}




