    <script>
        function show() {
            var Body = document.querySelector('body');
            var Toast = document.getElementById('toast');

            
            Toast.classList.add('show'); // Add the 'show' class to trigger the animation
        }

        function closePopUp() {
            var Body = document.querySelector('body');
            var Toast = document.getElementById('toast');

            Body.style.overflow = 'auto';
